import React, { Component } from 'react';
import PhoneInfo from './PhoneInfo';
class PhonInfoList extends Component {
    static defaultProps = {
        data: []
    }

    render() {
        const { data, onRemove, onUpdate } = this.props;

        //if (!data) return null;   defaultProps 대신에 사용 가능

        const list = data.map(
            info => (
                <PhoneInfo 
                    onRemove={onRemove}
                    onUpdate={onUpdate}
                    info={info}     
                    key={info.id} 
                />
            )
        );
        return (
            <div>
                {list}
            </div>
        );
    }
}

export default PhonInfoList;
